import React from "react";
import "../styles/promotions.css";

const PromoStrip = () => {
  return (
    <section className="promo-strip">
      <div className="promo-track">
        <div className="promo-group">
          <span className="promo-item">Nueva línea Oversize</span>
          <span className="promo-item">Envío gratis por compras superiores a $150.000</span>
          <span className="promo-item">10% OFF en colección NOIR</span>
        </div>

        {/* Duplicamos para efecto infinito */}
        <div className="promo-group">
          <span className="promo-item">Nueva línea Oversize</span>
          <span className="promo-item">Envío gratis por compras superiores a $150.000</span>
          <span className="promo-item">10% OFF en colección NOIR</span>
        </div>
      </div>
    </section>
  );
};

export default PromoStrip;
