import React from "react";
import "../styles/promotions.css";

const PromoDiscs = () => {
  const promos = [
    { text: "Nueva línea Oversize" },
    { text: "Envío gratis +$150.000" },
    { text: "10% OFF en colección Noir" },
  ];

  return (
    <section className="promo-discs-section">
      {promos.map((promo, index) => (
        <div key={index} className={`disc-wrapper float-${index}`}>
          <div className="pulse-ring"></div>
          <div className="promo-disc">
            <span>{promo.text}</span>
          </div>
        </div>
      ))}
    </section>
  );
};

export default PromoDiscs;
