import React from "react";
import "../styles/promotions.css";

const PromoSled = () => {
  const promos = [
    "Nueva línea Oversize",
    "Envío gratis por compras superiores a $150.000",
    "10% OFF en colección Noir",
  ];

  return (
    <section className="promo-sled-section">
      <div className="athlete">
        <div className="athlete-body"></div>
        <div className="athlete-arm"></div>
        <div className="athlete-leg"></div>
      </div>

      <div className="sled-container">
        {promos.map((promo, index) => (
          <div key={index} className="sled">
            <div className="sled-weight"></div>
            <div className="sled-text">{promo}</div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default PromoSled;
