import { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/orders.css';

function Orders() {
    const [orders, setOrders] = useState([]);
    const [userId, setUserId] = useState(null);
  
    useEffect(() => {
      const storedUser = JSON.parse(localStorage.getItem('user'));
      if (storedUser && storedUser._id) {
        setUserId(storedUser._id);
      } else {
        console.warn('‚ö†Ô∏è No hay usuario en localStorage');
      }
    }, []);
  
    useEffect(() => {
      if (!userId) return;
  
      const fetchOrders = async () => {
        try {
          const res = await axios.get(`http://localhost:3000/api/orders/user/${userId}`);
          console.log('üì¶ √ìrdenes tra√≠das:', res.data);
          setOrders(res.data);
        } catch (err) {
          console.error('‚ùå Error al obtener √≥rdenes:', err);
        }
      };
  
      fetchOrders();
    }, [userId]);
  
    return (
      <div className="orders-container">
        <h2>Mis √ìrdenes</h2>
        {orders.length === 0 ? (
          <p>No tienes √≥rdenes a√∫n.</p>
        ) : (
          orders.map((order) => (
            <div className="order-card" key={order._id}>
              <h3>Orden #{order._id.slice(-6)}</h3>
              <p><strong>Fecha:</strong> {new Date(order.createdAt).toLocaleDateString()}</p>
              <p><strong>Total:</strong> ${order.total}</p>
              <p><strong>Productos:</strong></p>
              <ul>
                {order.items.map((item, idx) => (
                  <li key={idx}>
                    {item.nombre} x{item.cantidad} - ${item.precio}
                  </li>
                ))}
              </ul>
            </div>
          ))
        )}
      </div>
    );
  }
  
  export default Orders;
