import React from "react";
import "../styles/promotions.css";

const PromoDiscs = () => {
  const promos = [
    { text: "Nueva línea Oversize", color: "#e63946" }, // rojo
    { text: "Envío gratis +$150.000", color: "#457b9d" }, // azul
    { text: "10% OFF en colección Noir", color: "#f1c40f" }, // dorado
  ];

  return (
    <section className="promo-discs-section">
      {promos.map((promo, index) => (
        <div key={index} className="disc-wrapper">
          <div className="pulse-ring" style={{ borderColor: promo.color }}></div>
          <div className="promo-disc" style={{ backgroundColor: promo.color }}>
            <span>{promo.text}</span>
          </div>
        </div>
      ))}
    </section>
  );
};

export default PromoDiscs;
