import React from 'react';
import '../styles/productCard.css';
import { FaShoppingCart } from 'react-icons/fa';

const ProductCard = ({ product, addToCart }) => {
  const handleAddToCart = (e) => {
    e.stopPropagation();
    addToCart(product);
  };

  return (
    <div className="w-[300px] bg-white shadow-md rounded-xl overflow-hidden flex flex-col justify-between group relative transition-transform hover:scale-[1.01]">
      {/* Imagen del producto */}
      <div className="w-full h-[340px] bg-gray-100 overflow-hidden flex justify-center items-center">
        <img
          src={`http://localhost:3000/uploads/${product.imagen[0]}`}
          alt={product.nombre}
          className="h-full object-cover"
        />
      </div>

      {/* Informaci√≥n */}
      <div className="p-4 text-center">
        <h3 className="font-bold text-lg text-black">{product.nombre}</h3>
        <p className="text-sm text-gray-600 mt-1">{product.descripcion}</p>
        <p className="font-semibold text-base text-black mt-2">${product.precio}</p>
      </div>

      {/* Icono flotante de carrito */}
      <div
        className="absolute bottom-4 right-4 bg-black text-white p-2 rounded-full opacity-0 group-hover:opacity-100 transition-opacity cursor-pointer"
        onClick={handleAddToCart}
        title="Agregar al carrito"
      >
        <FaShoppingCart size={18} />
      </div>
    </div>
  );
};

export default ProductCard;
