import React from "react";
import "../styles/promotions.css";

const promos = [
  "Nueva línea Oversize",
  "Envío gratis por compras superiores a $150.000",
  "10% OFF en colección Noir"
];

const PromoDiscs = () => {
  return (
    <section className="promo-section">
      {promos.map((promo, i) => (
        <div key={i} className="promo-disc">
          <span>{promo}</span>
        </div>
      ))}
    </section>
  );
};

export default PromoDiscs;
