import { useEffect, useState, useContext } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import axios from 'axios';
import { CartContext } from '../CartContext';
import '../styles/featuredCarousel.css';

const FeaturedCarousel = () => {
  const [featured, setFeatured] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    axios.get('http://localhost:3000/api/products/featured')
      .then(res => {
        if (Array.isArray(res.data)) {
          setFeatured(res.data);
        } else {
          console.error('Respuesta inesperada:', res.data);
        }
      })
      .catch(err => {
        console.error('Error al cargar productos destacados:', err);
      });
  }, []);

  const nextSlide = () => {
    setCurrentIndex((prev) => (prev + 1) % featured.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prev) => (prev - 1 + featured.length) % featured.length);
  };

  // productos visibles (3 a la vez en desktop)
  const getVisibleProducts = () => {
    if (featured.length === 0) return [];
    let visible = [];
    for (let i = 0; i < 3; i++) {
      visible.push(featured[(currentIndex + i) % featured.length]);
    }
    return visible;
  };

  return (
    <div className="carousel-container">
      <div className="carousel-wrapper">
        <AnimatePresence mode="wait">
          <motion.div
            key={currentIndex}
            className="carousel-track"
            initial={{ x: -50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: 50, opacity: 0 }}
            transition={{ duration: 0.6 }}
          >
            {getVisibleProducts().map(product => (
              <div className="carousel-card" key={product._id}>
                {product.imagen?.[0] && (
                  <img
                    src={`http://localhost:3000/uploads/${product.imagen[0]}`}
                    alt={product.nombre}
                  />
                )}
                <h3>{product.nombre}</h3>
                <p>{product.descripcion}</p>
                <p><strong>${product.precio.toLocaleString()}</strong></p>
                <button onClick={() => addToCart(product)}>
                  Agregar al carrito
                </button>
              </div>
            ))}
          </motion.div>
        </AnimatePresence>
      </div>

      <button className="carousel-arrow left" onClick={prevSlide}>⬅</button>
      <button className="carousel-arrow right" onClick={nextSlide}>➡</button>
    </div>
  );
};

export default FeaturedCarousel;
