import { motion } from "framer-motion";
import "../styles/promotions.css";

const promos = [
  "Nueva línea Oversize",
  "Envío gratis desde $150.000",
  "10% OFF en colección NOIR",
];

const floatingItems = [
  "/uploads/disc.png",
  "/uploads/kettlebell.png",
  "/uploads/dumbbell.png",
  "/uploads/bottle.png",
];

export default function PromoArena() {
  return (
    <section className="promo-arena">
      {/* Fondo animado con objetos fitness */}
      {floatingItems.map((item, i) => (
        <motion.img
          key={i}
          src={item}
          alt="fitness-item"
          className="floating-item"
          initial={{ x: Math.random() * 800, y: Math.random() * 500 }}
          animate={{
            x: [Math.random() * 800, Math.random() * 800],
            y: [Math.random() * 500, Math.random() * 500],
            rotate: [0, 360],
          }}
          transition={{
            duration: 20 + Math.random() * 10,
            repeat: Infinity,
            repeatType: "reverse",
          }}
        />
      ))}

      {/* Promociones en el centro */}
      <div className="promo-center">
        {promos.map((promo, i) => (
          <motion.div
            key={i}
            className="promo-text"
            whileHover={{ scale: 1.1, textShadow: "0px 0px 12px #FFD700" }}
          >
            {promo}
          </motion.div>
        ))}
      </div>
    </section>
  );
}
